function loadCustomFonts(){let t=document.querySelectorAll("[class^='emfont']"),e={};for(let n in t.forEach(t=>{let n=t.className.split(" ").find(t=>t.startsWith("emfont-")).replace("emfont-",""),o=t.textContent.trim();n&&o&&(e[n]=e[n]+o)}),e){var o=e[n];fetch("https://font.emtech.cc/g/"+n,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({words:o=Array.from(new Set(o.split(""))).sort().join("")})}).then(t=>t.json()).then(t=>{console.warn("emfont: 字體 "+t.font+" 已生成完成，若網頁沒有變動文字可以直接直接載入網址\n"+t.url+" 來使用。");let e=new FontFace(t.font,`url(${t.url})`,{style:t.style,weight:t.weight});document.fonts.add(e)})}}document.addEventListener("DOMContentLoaded",loadCustomFonts);