function loadCustomFonts(){let t=document.querySelectorAll("[class^='emfont']"),e={};for(let n in t.forEach(t=>{let n=t.className.split(" ").find(t=>t.startsWith("emfont-")).replace("emfont-",""),o=t.textContent.trim();n&&o&&(e[n]=e[n]+o)}),e){var o=e[n];fetch("http://127.0.0.1:3000/g/"+n,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({words:o=Array.from(new Set(o.split(""))).sort().join("")})}).then(t=>t.json()).then(t=>{let e=new FontFace(t.font,`url(${t.url})`,{style:t.style,weight:t.weight});document.fonts.add(e),e.load().then(e=>{document.fonts.add(e),element.style.fontFamily=t.font})})}}